package lab_2;
import java.lang.String;
public class Accept_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		    char ch[] = {'s','r','e','e','j','a'};
		    String s1 = new String(ch);
		    System.out.println("Character at index 2 is: "+s1.charAt(2));
		        System.out.println("Length of given string is: "+s1.length());
		        System.out.println("String after converting it to uppercase is: "+s1.toUpperCase());
		        System.out.println("String after converting it to lowercase is: "+s1.toLowerCase());
		        System.out.println("Substring of given string is: "+s1.substring(2,5));
		        System.out.println("Contains function: "+s1.contains(s1));
		    
		    

		  }

		}
		
